INSERT INTO `genus` (`created_at`, `updated_at`, `id`, `name`, `lat_name`, `description`, `plant_category_id`) VALUES ('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 1, 'пихта', 'Abies', 'Вечнозелёное дерево. В молодом возрасте медленно растущие.Требуют средне- хорошие, достаточно увлажненные почвы. Плохо переносит городские условия. Теневыносливо.pH-5,0-6,5\r\n', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 2, 'кипарисовик', 'Chamaecyparis', 'Вечнозелёное дерево  с низкой морозостойкостью, теплолюбивое . И средне-тенистые. Средне быстрорастущие. Требуют средне хорошие влажные почвы. pH 5,0-6,5\r\n', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 3, 'туя', 'Thuja', 'Вечнозелёные деревья или кустарники. Хорошо растут в городских условиях. Неприхотливые к почвам-растут как на сухих таки на влажных известковых почвах. Любят тень, но хорошо растут и на солнце. Очень большой выбор сортов по форме, размерам и цвету. pH 6,0-7,5', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 4, 'можжевельник', 'Juniperus', 'Вечнозелёное солнцелюбивое  дерево или кустарник с разнообразными размерами, формами и цветовыми вариациями. Выносливые и неприхотливые любят хорошо дренированную почву, pH 5,5-7,0. Высаживают по отде', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 5, 'лиственница', 'Larix', 'Хвойное солнцелюбивое, быстрорастущее, долговечное дерево с развитой корневой системой. Осенью полностью сбрасывает хвою. Требует плодородную, хорошо дренированную почву. Хорошо растёт в городах, но м', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 6, 'микробиота', 'Microbiota', 'Вечнозелёный кустарник, морозостойкий. Требует средне хорошие почвы. Влажные. Растёт на солнце, но лучше полутень. pH 5,5-7,0', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 7, 'ель', 'Picea', 'Вечнозелёное дерево с большим разновидностями в росте, сучьях, цветах, хвои и плодов. Требуют средне хорошие, достаточно влажные почвы. Корневая система не глубокая, обширная. В молодом возрасте любят', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 8, 'сосна', 'Pinus', 'Вечнозелёные деревья и кустарники. Сравнительно быстрорастущая, устойчивые против \r\nветров. Лучше растут на влажных, рыхлых, песчаных почвах. Солнцелюбивые. pH-5,5-7,0', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 9, 'псевдотсуга', 'Pseudotsuga', 'Вечнозелёные большие деревья с сильной корневой системой. Очень быстрорастущие, при обеспечении среднехорошей почвой и влагой. Любят слегка затенённое место. Пригодны для городских условий. pH 5,0-6,5', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 10, 'тис', 'Taxus', 'Вечнозелёные деревья или кустарники с плоской хвоёй. Лучше растут в рыхлой, богатой известью, глинистой почве. Двудомные растения. Плохая зимостойкость. Любят тень. Всё растение ядовитое. Медленнорастущее. pH 5,5-7,5', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 11, 'тсуга', 'Tsuga', 'Вечно зелёные деревья. Медленнорастущие. Хорошо растут во влажных глинистых почвах. Декоративные. В молодом возрасте любят тень. pH 4,5-6.5', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 12, 'абелия', 'Abelia', 'Летнезелёный или вечнозелёный кустарник. Выращивают в защищённых, солнечных местах на глинистых, песчано-глинистых известковых почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 13, 'клён', 'Acer', 'Лиственное дерево или речной куст. В природе растёт по берегам рек, в ложбинах, в плодородных известковых достаточно влажных почвах. Красивое растение в насаждениях, производитель нектара - медонос. Осенью листья окрашиваются в яркие цвета.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 14, 'актинидия', 'Actinidia', 'Лиана со стволом, которая крепится к основе, её обвивая. Однодомные или двухдомные, которые приносят плоды-ягоды (много сахара и витамина С). Для выращивания необходимы плодородные почвы. Непереносит засуху. Быстрорастущие. Лучше растёт на восточных и западных краях строений. Могут переносить весенние заморозки.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 15, 'каштан', 'Aesculus', 'Лиственное дерево. Средне быстрорастущее. Требует глубокие, рыхлые и достаточно влажные почвы. Применяются так же в уличных алеях. Некоторые растения медоносы. Любят солнечные места. Формовка нерекомендуется.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 16, 'ольха', 'Alnus', 'Лиственные быстрорастущие деревья или кустарники. В природе растут по краям водоёмов, одни или с берёзами, елям. Листопад улучшает почву. Светлолюбивые. Нелюбят обрезку.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 17, 'ирга', 'Amelanchier', 'Лиственный кустарник. Светлолюбивый. К почвам неприхотлив. Среднерастущий. Обильноцветущий. Можно стрич. Плоды употребляют в пищу.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 19, 'миндаль', 'Amygdalus', 'Лиственный кустарник. Цветёт одновременно с распусканием листьев. Морозостойкий на солнечных, защищённых местах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 20, 'аралия', 'Aralia', 'Небольшие деревья или кусты с терновыми стволами. Растут на хороших песчаных и глинистых почвах. Желательна полутень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 21, 'толокнянка', 'Arctostaphylos', 'Вечнозелёные стелящиеся карликовые кусты. Растут в сухих сосновых лесах. ', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 22, 'кирказон', 'Aristolochia', 'Лиственная лиана. Обвиваются стволом за основу. Хорошо переносят полутень. Среднебыстрорастущие. Необходима рыхрая богатая перегноем почва.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 23, 'абрикос', 'Armeniaca', 'Лиственные деревья. Солнцелюбивые. Относительно быстрорастущие. Необходима хорошодренированная песчаная или суглинистая почва богатая известью.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 24, 'арония', 'Aronia', 'Лиственный кустарник. Растёт на почвах богатых перегноем. Светолюбивый, устойчивый к засухе, средне быстрорастущий. Плоды употребляют в пищу и медецину. Осенью листья ярко оранжевые до ярко красных.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 25, 'полынь, божье дерево', 'Artemisia', 'Лиственные полукусты. Солнцелюбивые. Растут в нормальном песчаном грунте с повышенным содержанием извести.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 26, 'княжник', 'Atragene (Syn. Clematis)', 'Лиственные ланы. Прикрепляются с помощью ножек (черенков) листа. Растёт лучше в солнечных местах. Требуются тяжёлые известковые рыхлые почвы. Средне быстрорастущие.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 27, 'барбарис', 'Berberis', 'Лиственные колючие кустарники. Растут в сухих, солнечных местах, на опушках леса. Средне быстрорастущие. Хорошо поддаются формовке. Плоды съедобные.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 28, 'берёза', 'Betula', 'Лиственное дерево или кустарник. Средне быстрорастущее. В природе растёт только в берёзовых рощах или в смеси с обыкновенными породами ёлкой, сосной, осиной и др. Берёза угнетает рядом растущие деревья.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 29, 'брукенталия', 'Bruckenthalia', 'Вечнозелёный кустарник. Растёт в солнечных и полузатенённых местах', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 30, 'буддлея', 'Buddleia', 'Лиственный кустарник с низкой морозостойкостью. Быстрорастущий. Наиболее декоративный в пору цветения. Посадка в нормальные почвы. Светолюбивый. Цветёт на молодых побегах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 31, 'самшит', 'Buxus', 'Небольшие вечнозелёные кусты. Медленнорастущие. Переносят тень. Устойчивые к засухе. Хорошо поддаётся формовке. Ph-5,5-7,5. Зимой под воздействием прямых солнечных лучей могут обгореть листья.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 32, 'вереск', 'Calluna', 'Вечнозелёные полукусты. Растут в сосновых лесах, на вырубках, в сухих местах или в мховых болотах. Отличный медонос. Декоративные листья и цветы. Ph 4,5-6,0. Формирования кустов вереска производят весной.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 33, 'кампсис', 'Campsis', 'Лиственная лиана. Лучше растёт в достаточно влажных, солнечных, защищённых местах, в среднеплодородных почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 34, 'карагана', 'Caragana', 'Лиственный кустарник. Неприхотлив к почве, но в сырых, плоходренированных почвах, погибает. Светолюбивый, засухо устойчивый. Можно стрич.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 35, 'граб', 'Carpinus', 'Лиственное дерево, реже куст. Средне требователно к почве. Плохо переносит заболоченность. Можно использовать для высоких живых стриженых изгородей. Средне быстрорастущее, переносит тень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 37, 'катальпа', 'Catalpa', 'Лиственное дерево с зонтиковидной кроной. Солнцелюбивое со средней морозостойкостью. Средне быстрорастущее, выносливое к выхлопным газам, вредителям и болезням. Растёт в плодородных достаточно влажных почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 38, 'древогубец', 'Celastrus', 'Лиственная лиана. Быстрорастущая. Нетребовательна к почвам, но требует достаточную влажность. Хорошо переносит затенение. Не желательно сажать рядом с молодыми деревьями. Закручивается стволом вокруг основы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 39, 'черешня', 'Cerasus', 'Большие или меньшие лиственные деревья и кусты. Растут в саду на нормальных почвах с повышенным содержанием извести. Медленнорастущие до средне быстрорастущих. Солнцелюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 40, 'багрянник', 'Cercidiphyllum', 'Лиственные небольшие деревья. Цаеты двухдомные. Сажают в нормальную до песчаной и глинистой почвы. Солнцелюбивые, переносят частичною тень. Устойчивые против загазованности выхлопными газами, болезней, вредителей. Средне быстрорастущие.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 41, 'айва, хеномелес', 'Chaenomeles', 'Лиственный кустарник. Светлолюбивый, быстрорастущий. Применяют в декоративных насождениях и садоводстве. Цветёт перед распусканием листьев. Плоды-декоративные многосеменные яблоки.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 42, 'ракитник', 'Chamaecytisus', 'Небольшой лиственный кустарник. Бастрорастущий. Любит сухую песчаноглинистую почву.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 43, 'ломонос, клематис', 'Clematis', 'Древовидные или кустовидные лианы. Сажают на защищённых от ветра местах и плодородных почвах. Солнцелюбивые. pH 6.0-7.5. Красивоцветущие. Быстрорастущие. Корни и молодые стволы лакомство для грызунов.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 44, 'клетра', 'Clethra', 'Лиственный кустарник. Требует полутень, pH 5-5,5. Цветы в стоячих кистьях на концах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 45, 'пузырник', 'Coluthea', 'Лиственный кустарник. Быстрорастущий. Растёт на известковых, сухих, песчанных или глинистых почвах. Морозоустойчивость средняя. Плоды- надутые стручки с тонкими стенками.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 46, 'дёрен, свидина, кизил', 'Cornus', 'Лиственный кустарник. Очень быстрорастущий. Неприхотлив к почве. Красные, зелёные, жёлтые ветви сохраняют окраску и зимой. Можно стрич.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 47, 'лещина', 'Corylus', 'Лиственный кустарник с неглубокой корневой системой. Сравнительно тенелюбивый, но плоды образуются только на солнце. Любит известковую, богатую перегноем почву. При пересадке нужно добавлять почву из прежнего места роста. ', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 48, 'скумпия (париковое дерево)', 'Cotinus', 'Лиственный, высокий кустарник. Теплолюбивый. Светлолюбивый. Лучше растёт в известковых почвах. Средне быстрорастущий.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 49, 'кизильник', 'Cotoneaster', 'Лиственный или вечнозелёный кустарник. Средне быстрорастущий. Светолюбивый, устойчивый к засухе. Лучше растёт в плодородных, известковых почвах. Плоды ябловидные, косточковые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 50, 'боярышник', 'Crataegus', 'Лиственные кустарник или небольшие деревья. Средне быстрорастущий. Лучше растут на дренированных, известковых почвах в солнечных местах. Колючие виды используются для неприхотливых живых изгородей. Хорошо переносят формирование. Много болезней.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 52, 'айва', 'Cydonia', 'Лиственный кустарник или дерево. Растёт в нормальных песчаных почвах. Светолюбив. Морозоустойчивость средняя.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 53, 'жарновец, ракитник', 'Cytisus', 'Лиственный кустарник. Быстрорастущий. Светолюбивый. Растёт в нормальных и сухих песчаных почвах, в вересковых садах, на опушках леса', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 54, 'дабеция', 'Daboecia', 'Вечнозелёный кустарник. Растёт в песчаных почвах с добавлением торфа. Переносят частичную тень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 55, 'волчеягодник', 'Daphne', 'Вечнозелёный или лиственный кустарник. Средне быстрорастущий. Растёт во влажных, тенистых лиственных лесах и кустарниках с плодородной почвой. Ядовитое растение.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 56, 'дейция', 'Deutzia', 'Лиственный кустарник. Красивоцветущий, ароматный. Необходима солнечное,защищённое место и плодородная, дренированная почва. Быстрорастущий. Морозоустойчивость средняя.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 57, 'диервилла', 'Diervilla', 'Лиственный кустарник. Растёт в нормальных песчаных и глинистых почвах. Светлолюбивый. Быстрорастущий.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 58, 'лох', 'Elaeagnus', 'Вечнозелёные или лиственные деревья и кустарники. В отношении почвы не разборчивы, растут и в частичной тени. Быстрорастущие. Хорошо растут в городских условиях. Можно использовать для укрепления песчаных берегов и склонов.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 59, 'шикша, водянка', 'Empetrum', 'Вечнозелёный вересковый карликовый кустарник с игольчатыми листьями. Растёт в лёгких, кислых песчаных и торфяных почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 60, 'эрика', 'Erica', 'Вечнозелёный кустарник. Растёт в песчаных почвах с добавлением торфа. Морозостойкость средняя. Устойчивость к выхлопным газам хорошая, переносит частичную тень и известковые почвы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 61, 'бересклет', 'Euonymus', 'Лиственные или вечнозелёные кустарники или маленькие деревья. Растут в плодородных, содержащих перегной, нейтральных или известковых почвах. Теневыносливы. Влаголюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 62, 'экзохорда', 'Exochorda', 'Лиственный кустарник. Лучше растёт на известковых сухих песчаных или глинистых почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 63, 'бук', 'Fagus', 'Лиственные деревья. Требуют хорошую влажную, богатую перегноем почвую Теневыносливы. В раннем возрасте растут медленно. Можно использовать для высоких живых изгородей.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 64, 'форзиция', 'Forsythia', 'Лиственный кустарник. Быстрорастущий. Обильно цветёт перед распусканием листьев. Не прихотлив к почве. Морозостойкость средняя.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 65, 'ясень', 'Fraxinus', 'Лиственные деревья с сильной корневой системой. Быстрорастущие. Светолюбивые. Растут на богатых перегноем, влажных почвах. Обрезка не желательна. Морозоустойчив.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 66, 'гаультерия', 'Gaultheria', 'Вечнозелёный кустарник. Растёт на песчаных почвах с примесью торфа. Любит раскинутся, даёт корневые побеги. Люби полутень или тень в достаточно влажном месте. Листья похожие на бруснику.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 67, 'дрок', 'Genista', 'Лиственный, низкий кустарник или полукустарник. Растёт на нормальных, сузих, известковых песках или глинистых почвах. Светолюбивый. В тени меньше цветёт. Быстрорастущий.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 68, 'дрок', 'Genistella', 'Лиственные карликовые кусты с широкими, ползучими, зелёными побегами. Светолюбивые. Почвопокровное растение. Любят песчаную, нормальную почву.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 69, 'гамамелис', 'Hamamelis', 'Лиственный кустарник или небольшое дерево. Растёт в нормальных, свежих, плодородных, песчаноглинистых почвах. Светолюбивый, переносит частичную тень. Медленнорастущий. Декоративный. Цветёт поздней осенью.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 70, 'плющ', 'Hedera', 'Зимозелёный лиановидный кустарник с стелящимися или восходящими (поднимающимися) стволами. К вертикальной поверхности прикрепляется с помощью захватывающих корней. Растёт в тенистых лесах, оврагах. Морозостойкость средняя. Почвопокровное растение.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 71, 'солнцецвет', 'Helianthemum', 'Вечнозелёные или полувечнозелёные карликовые кусты с стелющимся стволом и сучьями, которые пускают корни. Образуют сильный центральный корень. Растут в сухих, солнечных местах на известковых почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 72, 'облепиха', 'Hippophae', 'Небольшие лиственные деревья или кустарники с колючими сучьями. Быстрорастущие. Солнцелюбивые. Используют и как плодовые растения.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 73, 'гортензия', 'Hydrangea', 'Лиственный, красивоцветущий, кустарник. Относительно медленнорастущий. Требует глубокие, рыхлые, питательные, влажные почвы. Зимостойкость средняя. Солнцелюбивый, но переносит и полутень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 74, 'зверобой', 'Hypericum', 'Вечнозелёные или лиственные полукустарники или кустарники, или травенистые растения. Хорошо растут как в песочных так и в глинистых, достаточно влажных, но рыхлых почвах. Светолюбивые, но переносят и тень. Зимой обмерзают, но хорошо затем отрастают.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 75, 'падуб', 'Ilex', 'Вечнозелёный или листопадный кустарник. Сажают в достаточно влажные, богатые перегноем почвы. Любит полутень или тень. Если после посадки опадают листья, обычно они быстро отрастают.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 76, 'орех', 'Juglans', 'Лиственные деревья. Декоративные с большими красивыми листьями и зонтикоподобной кронай. Быстрорастущие. Любят плодородные, хорошодренированные почвы. Образует центральный корень. Хорошо переносят городские условия. Светолюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 77, 'керия', 'Kerria', 'Лиственный кустарник. Необходимо сажать в нормальные, известковые, глинистопесчаные и песчаноглинистые почвы. Светолюбивый. Средне быстрорастущий. Декоративный. Есть корневые побегию', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 78, 'колквиция', 'Kolkwitzia', 'Лиственный кустарник. Растёт в хорошей садовой почве. Светолюбивый. Декоративный во время цветения и красочной листвой осенью.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 79, 'бобовник', 'Laburnum', 'Не большие, красиво цветущие деревья или кусты. Средне быстрорастущие. Любят хорошие песчаноглинистые, известковые почвы. Зимостойкость средняя. Светолюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 80, 'бирючина', 'Ligustrum', 'Лиственный или вечнозелёный кустарник. Не прихотлив к почве. Желательна небольшая тень. Засухоустойчив. Очень хорошо поддаётся искуственному формированию.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 81, 'жимолость', 'Lonicera', 'Прямостоящие, стелящиеся или вьющиеся лиственные или вечнозелёные кустарники. Лучше растут на плодородных дренажных почвах в солнечных местах. Переносят и тень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 82, 'магнолия', 'Magnolia', 'Листопадные или вечнозелёные деревья и кустарники. Средне быстрорастущие. Зимостойкость средняя. Сажают в защищённых местах. Желательно сажать в подготовленные, ухоженые, глинистые садовые почвы с достаточной влагой. Весной почву нужно мульчировать. Лучше чувствует в присутствии других деревьев и в частичном затенении.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 83, 'магония', 'Mahonia', 'Вечнозелёный кустарник. Любит лёгкие почвы с достаточной влагой. Необходима частичная тень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 84, 'яблоня', 'Malus', 'Лиственные деревья или кусты. Для декоративных целей выращивают сорта с красивыми цветами и яркими мелкими плодами. Растут в хорошо дренированных песчаноглинистых почвах. Светолюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 85, 'мелкоплодник', 'Micromeles', 'Лиственные деревья. Очень похожи на рябину.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 86, 'шелковое дерево', 'Morus', 'Лиственное дерево или кустарник. Растёт в хороших, глубоких, богатых известью почвах. Светолюбивое. Морозостойкость средняя. Средне быстрорастущее.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 87, 'нотофагус', 'Nothofagus', 'Лиственный кустарник, реже дерево. В отношение почвы не прихотлив, переносит кислые почвы, высокие грунтовые воды. Нужно сажать в солнечных местах, в полутени.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 88, 'пахисандра', 'Pachysandra', 'Вечнозелёное почвопокровное растение. Растёт в рыхлых, богатых перегноем почвах. Любит тень и полутень. Длинные побеги наклоняются к земле и пускают корни.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 89, 'черёмуха', 'Padus', 'Лиственные деревья или кусты. Растут по берегам рек, на опушках леса. Растущие в тени обычно не цветут. Выносливы к выхлопным газам но относительно много вредителей.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 90, 'виноград девичий', 'Parthenocissus', 'Лиственная древовидная лиана. Быстрорастущая. Малотребовательна к почве. Выносливы к выхлопным газам. Растут в полутени и на солнце.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 91, 'периплок', 'Periploca', 'Полукустарник или кустарниковая лиана. Лучше растёт в солнечных местах. Требуют тяжёлые, дренированные, известковые почвы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 92, 'бархат', 'Phellodendron', 'Лиственные деревья с хорошо развитой корневой системой. Двудомные растения. Поздно распускаются и рано сбрасывают листья. Быстрорастущие. Морозостойкие. Светолюбивые, но переносят тень в молодости.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 93, 'чубушник', 'Philadelphus', 'Лиственные кусты с белыми, душистыми цветами. Растут в почвах богатых перегноем, хорошо дренированных. Светолюбивые, но переносят и затенение. Быстрорастущие. Морозостойкость средняя.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 94, 'пузыреплодник', 'Physocarpus', 'Лиственный кустарник. Не прихотлив к почвам. Быстрорастущий. Светолюбивый, но переносит и тень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 95, 'пиерис', 'Pieris', 'Не большой вечнозелёный кустарник. Растёт во влажных, богатых пергноем и воздухом кислых почвах. Любит полутень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 96, 'осина, тополь', 'Populus', 'Лиственные, быстрорастущие двухдомные деревья. Переносят загазованность. Сильная корневая система. У растений с мужскими цветами  отсутствует пух.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 97, 'лапчатка, курильский чай', 'Potentilla', 'Лиственные кусты. Зимостойкие. Средне быстрорастущие. Устойчивые к загазованности. Относительно не прихотливы к почвам. Светолюбивые. Обильно и долго цветут.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 98, 'слива', 'Prunus', 'Небольшие деревья или кусты. Светолюбивые. Не переносят кислые, влажные почвы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 99, 'птелея', 'Ptelea', 'Лиственный кустарник с ароматной корой и листьями. Растёт в нормальные песчано-глинистых почвах. Средне быстрорастущее. Зимостойкий. Светолюбивый. Вредителей и болезней нет.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 100, 'пираканта', 'Pyracantha', 'Вечнозелёный колючий кустарник. Высаживают в нормальные садовые почвы. Светолюбивый, но весной лучше затенить. Медленнорастущий.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 101, 'дуб', 'Quercus', 'Лиственные или вечнозелёные деревья с сильной корневой системой. Требует богатые перегноем, дренированные, глубокие почвы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 102, 'крушина', 'Rhamnus', 'Лиственные деревья или кусты. Для выращивания требуются плодородные, умеренно влажные почвы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 103, 'рододендрон', 'Rhododendron', 'Вечнозелёные или листопадные красиво цветущие кусты. Высаживают в песчаные или торфяные, водопроницаемые, рыхлые, богатые перегноем, кислые (pH-4,0-6,0) почвы. Вечнозелёные требуют большую тень, особенно весной. Листопадные обильно и красиво цветут в солнечных местах с частичной полутенью. Медленнорастущие.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 104, 'розовик', 'Rhodotypos', 'Лиственный кустарник. Нужно сажать в хорошие почвы. Любит полутень. Средне быстрорастущий. Декоративный.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 105, 'сумак', 'Rhus', 'Лиственные или вечнозелёные деревья или кусты. Быстрорастущие. Не прихотливые к почве. Засухоустойчивые. Светолюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 106, 'смородина', 'Ribes', 'Лиственный кустарник. В плодовой культуре много сортов красной смородины. В декоративных насаждениях используют растения, которые пригодны для формирования или красиво цветут. Растут в нормальных, достаточно влажных садовых почвах. Преносят тень. Устойчивы  к загазованности.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 107, 'робиния', 'Robinia', 'Лиственные деревья или кусты. Светолюбивые. Засухоустойчивые, с глубокой корневой системой. Не прихотливы к почве (не растут в торфе). Быстрорастущие. На открытых местах страдают от ветра (ломаются).', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 108, 'роза', 'Rosa', 'Лиственные прямостоящие или вьющиеся кусты. Растут в нормальных, хорошо дренированных, желательно известковых почвах. Светолюбивые. Устойчивы к загазованности. Много вредителей.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 109, 'малина, ежевика', 'Rubus', 'Лиственные стелящиеся или прямостоящие кусты. Растут по вырубкам, берегам рек, на опушках леса и вдоль дорог. Растения с чёрными плодами называют ежевикой, а красными плодами - малиной.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 110, 'ива', 'Salix', 'Лиственные, реже вечнозелёные деревья или кусты. Быстрорастущие. Малотребовательны к почве. Используют так же в плетении. Особенно декоративны повислые формы и формы с серебристой листвой, а так же карликовые формы.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 111, 'бузина', 'Sambucus', 'Лиственные кусты или небольшие деревья. Не прихотливые к выбору почвы, но любят нормальную известковую почву. Теневыносливы, но растут и на солнце. Ягоды очень нравятся птицам.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 112, 'лимонник', 'Schisandra', 'Листпадные или нет лианы. Одно- или двухдомное растение. Высаживают в хорошие, достаточно влажные плодородные почвы, полутень в зоне стен строений. Средне быстрорастущее. Зимостойкое.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 113, 'сибирка', 'Sibiraea', 'Лиственные низкие двудомные кусты. Растут в нормальных влажных почвах. Быстрорастущие. Зимостойкие. Светолюбивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 114, 'рябина', 'Sorbus', 'Лиственные деревья или кусты. Растут в богатых почвах. Местные сорта не прихотливы. Светолюбивые, но переносят частичную тень. Зимостойкие. Листья осенью окрашиваются в багрово-оранжевые тона.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 115, 'спирея, таволга', 'Spiraea', 'Лиственные кусты. Красиво цветущие в разное время. Мелкая, разветвлённая корневая система. Хорошо растут в нормальных, известковых почвах. Светолюбивые. Засухоустойчивые.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 116, 'стефанандра', 'Stephanandra', 'Лиственный небольшой кустарник. Растёт в нормальных почвах. Средне быстрорастущий. Светолюбивый.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 117, 'снежноягодник', 'Symphoricarpos', 'Лиственные кусты. Не прихотливы к выбору почвы. Относительно переносят тень. Декоративны осенью во время плодоношения.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 118, 'сирень', 'Syringa', 'Большие лиственные кусты. Растут в хороших, богатых питательными веществами, почвах. Светолюбивые. Засухоустойчивые. У сортов разное время цветения и раскраска цветов. ', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 119, 'тимьян', 'Thymus', 'Вечнозелёные полукусты. Светолюбивые. Любят лёгкие, хорошо дренированые почвы. Используют для альпийских горок.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 120, 'липа', 'Tilia', 'Лиственные деревья с сильной корневой системой. Растут в нормальных, дренированных, богатых перегноем почвах.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 121, 'триптеригия', 'Tripterygium', 'Лиственные лианы. Вьются по опорам. Декоративный ствол и корень.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 122, 'вяз, ильм', 'Ulmus', 'Лиственное дерево. Растёт в нормальных почвах, не переносит излишнюю влагу. Относительно быстрорастущее. Много вредителей и болезней.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 123, 'брусника, клюква, голубика', 'Vaccinium', 'Вечнозелёные или листопадные полукусты или кусты. Растут в кислых, торфяных, разной влажности почвах. Плоды используют в пищу.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 124, 'калина', 'Viburnum', 'Лиственные и вечнозелёные кусты. Быстрорастущие. Растут в нормальных, богатых перегноем почвах. Переносят частичную тень. Сорта со съедобными ягодами.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 125, 'барвинок', 'Vinca', 'Вечнозелёные стелящиеся полукусты или травянистое растение. Растут в богатых питательными веществами почвах. Любят полутень или теь. У ряболистных сортов зимостойкость хуже. ', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 126, 'виноград', 'Vitis', 'Лиственная лиана. Быстрорастущая. Растёт в хороших, достаточно тяжёлых почвах. Нельзя обрезать весно. Широки выбор культурных сортов. Прикрепляется к опоре усиками.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 127, 'вейгела', 'Weigela', 'Лиственный кустарник. Обильно и красиво цветущий. Быстрорастущий. Растёт в дренированных плодородных почвах. В молодости желательна полутень. Зимостойкость среднехорошая, у сортов хуже.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 131, 'аморфа', 'Amorpha', 'Лиственный кустарник.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 130, 'груша', 'Pyrus', 'Лиственные деревья. Обильно плодоносят.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 132, 'рябинник', 'Sorbaria', 'Листопадный кустарник до 3 м высотой с изящными, крупными, перистыми листьями, которые в начале вегетации имеют оранжевый оттенок, а осенью окрашиваются в карминово-красный цвет. Цветки белые, многочисленные, собраны в крупные метелки. Образует густые, очень эффектные заросли. Обладает высокой побегообразовательной способностью и быстро растет в высоту. Нетребователен к почве, но предпочитает влажную, хорошо дренированную. Выносит небольшое затенение. Обладает фитонцидными свойствами.\r\n', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 133, 'тамарикс', 'Tamarix', 'Лиственный кустарник или небольшое дерево. Мелкие белые или розовые цветки собраны в крупные кистевидные соцветия. Засухоустойчив, светолюбив, нетребователен к почве, но предпочитает известковую. Не переносит застоя воды. Легко переносит стрижку и пересадку. Выдерживает условия города. Может подмерзать, но быстро восстанавливается и цветет. Является одной из лучших пород для закрепления песков, особенно засоленных.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 134, 'гинк', 'Ginkgo', 'Летнезелёные большие деревья. Требуют песчаную почву. pH 5-7. Переносят городские условия. Весной можно искусственно формировать.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 135, 'метасеквойя', 'Metasequoia', 'Хвойное дерево.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 136, 'таксодиум', 'Taxodium', 'Хвойное дерево.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 137, 'туевик', 'Thujopsis', 'Хвойный кустарник.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 138, 'кедр', 'Cedrus', 'Крупное вечнозелёное дерево. Хорошо растут на увлажнённых, аэрированных почвах.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 139, 'сциадопитис', 'Sciadopitys', 'Хвойное растение.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 140, 'азалия', 'Azalea', 'Лиственный красивоцветущий кустарник.', 1),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 142, 'Биота', 'Biota', 'Хвойное растение родом из Северного Китая. Широко распространена в озеленении южных р-нов России. В роду один вид. Имеет много декоративных форм.', 2),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 145, 'Тысячелистник', 'Achillea', 'Многолетние корневищные травы, реже — полукустарники. Род Тысячелистник включает около 150 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 146, 'Ацена', 'Acaena', 'Стелющийся кустарник высотой от 10 до 30 см, с  зимующими листьями. В природе существует около 60 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 147, 'Живучка', 'Ajuga', 'Однолетние и многолетние травянистые цветущие растения. Род насчитывает около 50 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 148, 'Манжетка', 'Alchemilla', 'Многолетнее кустистое прямостоячее травянистое растение высотой 45—50 см. Листья очень декоративны.\r\nЦветки зеленовато-жёлтые или беловатые.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 149, 'Частуха', 'Alisma', 'Многолетнее травянистое растение для водоемов и прибрежных зон. Цветки белые или розовато-белые, с тремя лепестками. В России произрастает 6 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 150, 'Алиссум', 'Alyssum', 'Однолетние и многолетние низкорослые цветущие растения. Цветки мелкие  с медовым запахом, белые, жёлтые или лиловые. Существует более 100 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 151, 'Анафалис', 'Anaphalis', 'Многолетнее цветущее растение семейства астровых высотой 20-50 см. Цветки белого и розового цвета. Насчитывает  35 разновидностей. Используется для срезки и составления сухих букетов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 152, 'Анхуза', 'Anchusa', 'Многолетние, двухлетние и однолетние цветущие травянистые растения высотой 20-60 см. Цветки голубые или пурпуровые, реже белые или желтые. Род насчитывает до 40 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 153, 'Ветреница', 'Anemone', ' Многолетнее травянистое цветущее растение, семействе Лютиковые. Цветки яркой окраски (белой, розовой, голубой, синей или желтой).  В России произрастает 46 видов, всего известно около 100. ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 154, 'Кошачья лапка', 'Antennaria', 'Многолетнее травянистое цветущее растение семейства Астровых высотой 10 - 50 см. Известно 45 видов. Применяется в медицине и декоративном садоводстве.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 155, 'Водосбор', 'Aquilegia', 'Травянистое многолетнее цветущее растение семейства Лютиковые высотой до 1м.  Цветки простые или махровые, от чисто-белой и золотисто-желтой до сиреневой и розовой окраски. Около 35 видов введено в культуру. ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 156, 'Арабис', 'Arabis', 'Многолетнее травянистое почвопокровное цветущее растение до 30 см высотой. Цветки белые, розовые, лиловые, красные простые и махровые, мелкие. Является медоносом. Известно около 120 видов.\r\n', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 157, 'Полынь', 'Artemisia', 'Растения многолетние, реже однолетние или двухлетние, травянистые или полукустарниковые. Насчитывается около 400 видов. Некоторые виды имеют декоративную и лекарственную ценность. ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 158, 'Волжанка', 'Aruncus', 'Многолетнее травянистое высокое растение с красивыми перистыми листьями и крупными соцветиями-метелками белого или кремового цвета. В естественной среде встречаются около 10-12 видов', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 159, 'Копытень', 'Asarum', 'Род многолетних низкорослых цветущих трав семейства кирказоновых.  Цветёт очень рано. У цветка довольно редкая окраска – коричневая с красноватым оттенком. В России  встречаются 3 вида. ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 160, 'Астра', 'Aster', 'Однолетние и многолетние  цветущие  травы  семейства Астровые высотой 20 - 70 см. Бывают махровые и простые. Существует огромное количество сортов. Цветы бывают белые, жёлтые, розовые, красные, синие, фиолетовые и т. д.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 161, 'Астильба', 'Astilbe', 'Многолетнее травянистое цветущее растение  семейства Камнеломковые  высотой от 8 до 200 см.  Мелкие цветки, белые, розовые, сиреневые, красные или пурпурные, собраны в верхушечные соцветия -метелки различной длины. Известно более 30 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 162, 'Астранция', 'Astrantia', 'Многолетнее травянистое цветущее растение семейства Зонтичные высотой 15 -90 см. Цветы многочисленные и мелкие, розово-лиловой гаммы. Известно около 10 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 163, 'Папоротник', 'Athyrium', NULL, 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 164, 'Обриета', 'Aubrieta', 'Стелющиеся многолетние цветущие вечнозелёные травянистые растения  семейства Капустные. Цветки розового, фиолетового или белого цвета. Род насчитывает порядка 10 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 165, 'Бадан', 'Bergenia', 'Вечнозелёные многолетние, реже однолетние цветущие растения высотой 6—35 см. семейства Камнеломковые. Цветки красные, розовые или белые. В настоящее время известно около 20 видов.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 166, 'Брунера', 'Brunnera', 'Многолетние травянистые цветущие растения с  цветками, похожими на незабудки семейства  Бурачниковые. Высота растений - 30 - 50 см. Известно 3 вида. ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 167, 'Воробейник', 'Buglossoides', 'Стелющееся многолетнее цветущее растение семейства Бурачниковые  высотой  15 - 50 см.  Цветки фиолетовые, голубые, пурпурные или белые. Известно в природе 4 вида.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 168, 'Сусак', 'Butomus', 'Многолетнее водяное цветущее растение единственный представитель семейства Сусаковые высотой до 100см. Цветки нежно розовые с яркими вкраплениями.   ', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 169, 'Вейник', 'Calamagrostis', 'Травянистые многолетние  растения семейства Злаки высотой до 150 см. Известно около 260 видов, в России произрастает около 50.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 170, 'Клиноног', 'Calamintha', 'Многолетнее кустистое цветущее растение семейства Губоцветные высотой до 100 см. Цветы мелкие с пурпурным оттенком. Применяется в медицине.', 5),
('2013-05-27 10:45:26.144883','2013-05-27 10:45:26.144883', 171, 'Калужница', 'Caltha', ' Многолетние цветущее растения семейства Лютиковые высотой до 40 см. Цветки желтые. Род включает около 40 видов', 5),
