INSERT INTO `rplants_foreign_members` (`fmember_id`, `ftype_member_id`, `country_id`, `f_name_rus`, `f_name_lat`, `f_address`, `f_info_mini`, `f_info`, `f_email`, `f_website`, `f_phone`, `f_services`) VALUES (2, 1, 4, 'Lorenz von Ehren', '', 'Maldfeldstrasse 4, D-21077 Hamburg', 'Выращивание садово-парковых растений. Есть каталог на русс. яз. с условиями поставок,  ассортим. растений и ценами.', '', 'LvE@LvE.de', 'www.LvE.de', '+49(40)76 1080', ''),
(3, 1, 13, 'Baltezers', '', 'Adazu pag., Rigas raj.', 'Ведущий питомник Латвии. Большой ассортимент лиственных и хвойных декоративных растений, цветов и трав.', '', 'baltezers@baltezers.lv', 'www.baltezers.lv', '+371 799 63 70', ''),
(4, 1, 16, 'Jan Cieplucha', '', '', 'Выращивание зимостойких сортов рододендронов и азалий.', 'Весь материал выращивается в теплицах (не отапливаемых). Многие сорта хорошо зимуют в нашем регионе (Санкт-Петербург, Москва). Качество материала очень приличное. Опыт торговли с Россией есть. Много красочного рекламного материала (плакаты, буклеты и т.д.). Есть рускоговорящий менеджер.', 'jan@cieplucha.com.pl', 'www.cieplucha.com.pl', '+48 42 211 18 00', ''),
(5, 1, 16, 'Nowy Dwor', '&quot;Новы Двур&quot; Питомник декоративных растений', '96-115 Новы Кавэнчин (Nowy Kaw&#281;czyn)', 'Выращивание декоративных растений и цветов. Ежегодно мы производим более миллиона хвойных и лиственных кустарников, аллейных и парковых деревьев .\r\n', 'Лариса Сломка, коммерческий директор', 'kzd@kzd.com.pl  lora@kzd.com.pl', 'www.kzd.com.pl', '+48 46/833 36 54  833 36 52', ''),
(6, 1, 4, 'Bruns Pflanzen-Export GmbH & Co.KG', '', 'Johann-Bruns-Allee 1, D-26160 Bad Zwischenahn, Postfach 11 65, D-26146 Bad Zwischenahn', 'Питомник, более 130 лет известный в Европе, выращивает и продает различные виды цветов, растений, деревьев, кустарников, почвопокровных растений для ландшафтного озеленения и садовых центров. На территории более 500 га выращиваются свыше 4000 видов и сортов растений. ', '', 'bruns@bruns-pflanzen.de', 'http://www.bruns.de/', '(0 28 31) 24 67', ''),
(7, 1, 4, 'H.Lorberg Baumschulerzeugnisse GmbH&Co.KG', '', 'Zachower Stra&#223;e 4, D-14669 Ketzin OT Tremmen,', 'Питомник  известен более 150 лет на рынке производства и продажи растений. Расположен на 400 га, культивирует древесные растения и кустарники., почвопокровные, травянистые многолетние растения.\r\nПосле объединения Германии, в 1991 году, все производство переводится из западногерманских земель в берлинское предместье, в Треммен. В 2002 году был открыт филиал питомника Лорберг - садовый центр в Кляйнцитене (в 5-ти км от аэропорта г. Берлина - Sch&#246;nenfeld) c целью презентации частным покупателям и ландшафтным архитекторам из Берлина и земли Бранденбург обширного ассортимента растений и других товаров.\r\n', '', 'lorberg@lorberg.com/', 'www.lorberg.com', '(03 32 33) 84-0', ''),
(8, 1, 19, 'Nichols Garden Nursery', '', '', 'Снабжает заказчиков семенами и растениями по почте в течение более чем 50 лет,  разыскивает самые прекрасные растения со всех континентов.', '', '', 'http://www.nicholsgardennursery.com/', '1 (800) 422-3985   Fax: 1 (800) 231-5306', ''),
(9, 1, 2, 'English Cottage', '', '', 'Выращивает разнообразные дикие цветы, травы и садовые растения – большинство из которых не так легко найти в садоводческих центрах, например редкие виды диких растений.', '', '', 'http://www.englishplants.co.uk/', 'Fax/Tel: 01227 730242', ''),
(10, 1, 2, 'Eastgrove', '', '', '– расположен в Worcestershire и является открытым для посещений. Мы специализируется в культивировании выносливых растений. Питомник Eastgrove входит в Королевское Садоводческое общество (RHS).\r\nСредиособенностей: Aquilegias, Hardy Chrysanthemums, Dianthu', '', '', '<http://www.eastgrove.co.uk/', '', ''),
(11, 2, 9, 'Японское общество садоводов', 'Japan Gardening Society', 'Главное бюро: 14-3, Moto Yoyogi-cho, Shibuya-ku Токио 151, Япония', '(основано в 1972 году) – Питомники, растения, обмен опытом, технологиями растениеводства, различные заочные курсы и многое другое.\r\nВыпускает бюллетень «BelleVie».\r\n', '', '', 'http://www.gardening.or.jp/e-index.html', 'Телефон 03-3465-5171   Fax:03-3465-0051', ''),
(12, 1, 5, 'Pistoia Piante', '', 'Pistoia Piante Via B.Sestini, 244    51030 PONTENUOVO (PT) - ITALY', 'Специализируется на выращивании различных декоративных уличных растений, предлагает очень широкий выбор – от небольших растений в вазоне до различных кустарников и деревьев. (вечнозеленые, хвойные, плодовые,пальмовые, цитрусовые, различные цветы и травы', '', 'info@pistoiapiante.it', 'http://www.pistoiapiante.it/', 'Tel. 39 0573 935307 - Fax. 39 0573 934239', ''),
